import React, { useState } from 'react';
import {
	Box,
	StackDivider,
	VStack,
	Heading,
	Text,
} from '@chakra-ui/react';
import MainContainer from './MainContainer'

const Recipe = (props) => {
	return (
		<Box p={5} shadow='md' borderWidth='1px' props={props}>
			<Heading fontSize='x1'>{props.title}</Heading>
			<Text mt={4}>{props.author}</Text>
		</Box>
	)
}

const MyRecipes = () => {

	let myLs = localStorage.getItem('recipe');
	let ls = JSON.parse(myLs)

	return (
		<>
			<MainContainer children={<Recipe title={ls.title}/>}/>
		</>
	)
}

export default MyRecipes;
