import React from 'react';
import { useState } from 'react';
import {
	Box,
	Center,
	FormControl,
	FormLabel,
	FormErrorMessage,
	FormHelperText,
	Input,
	Button
} from '@chakra-ui/react';

const RegisterPage = () => {

	const [login, setLogin] = useState('');
	const [password, setPassword] = useState('');


	return (
		<Center>
			<Box>
				<FormControl id='login' isRequired>
					<FormLabel>Login</FormLabel>
					<Input type='login' variant='flushed' size='sm' onChange={e => setLogin(e.target.value)}/>
				</FormControl>
				<FormControl id='password' isRequired>
					<FormLabel>Password</FormLabel>
					<Input type='password' variant='flushed' size='sm' onChange={e => setPassword(e.target.value)}/>
					<FormHelperText>Remember to never share your password with anybody</FormHelperText>
				</FormControl>
				<Button colorScheme='teal' variant='outline' onClick={localStorage.setItem('credentials', [login, password])}>Register</Button>
			</Box>
		</Center>
	)
}

export default RegisterPage;
