import React from 'react';
import { useState, useEffect } from 'react';
import {
	Box,
	Center,
	FormControl,
	FormLabel,
	// FormErrorMessage,
	FormHelperText,
	Button,
	Input
} from '@chakra-ui/react';

const RegisterPage = () => {

	const [login, setLogin] = useState('');
	const [password, setPassword] = useState('');

	useEffect(() => {
		localStorage.setItem('login', login)
	}, [login])

	const setLocalStorage = () => {
		localStorage.setItem('login', login)
		localStorage.setItem('password', password)
		console.log(localStorage)
	}

	return (
		<Center>
			<Box>
				<FormControl id='login' isRequired>
					<FormLabel>Login</FormLabel>
					<Input type='login' variant='flushed' size='sm' onChange={e => setLogin(e.target.value)}/>
				</FormControl>
				<FormControl id='password' isRequired>
					<FormLabel>Password</FormLabel>
					<Input type='password' variant='flushed' size='sm' onChange={e => setPassword(e.target.value)}/>
					<FormHelperText>Remember to never share your password with anybody</FormHelperText>
				</FormControl>
				<Button colorScheme='teal' variant='outline' onClick={setLocalStorage()}>Register</Button>
			</Box>
		</Center>
	)
}


export default RegisterPage;


