import React from 'react';
import { useState, useEffect } from 'react';
import { useFormik } from 'formik';
import {
	Box,
	Center,
	FormControl,
	FormLabel,
	FormErrorMessage,
	FormHelperText,
	Button,
	Input,
	Checkbox,
} from '@chakra-ui/react';

const RegisterPage = () => {

	const [login, setLogin] = useState('');
	const [password, setPassword] = useState('');

	useEffect(() => {
		localStorage.getItem('login', 'password')
	}, [login, password])

	const setLocalStorage = () => {
		localStorage.setItem('login', login)
		localStorage.setItem('password', password)
		console.log(localStorage)
	}

	const clearLocalStorage = () => {
		localStorage.clear();
		console.log(localStorage)
	}

	const formik = useFormik({
		initialValues: {
			login: '',
			password: ''
		},
		onSubmit: values => {
			console.log(JSON.stringify(values, null, 2));
		},
	});
	return (
		<Center>
			<Box>
			<form onSubmit={formik.handleSubmit}>
				<FormControl id='login' isRequired>
					<FormLabel htmlFor='login'>Login</FormLabel>
					<Input 
						type='login' 
						variant='flushed' 
						size='sm' 
						id='login'
						name='login'
						onChange={formik.handleChange}
						value={formik.values.login}
						/>
				</FormControl>
				<FormControl id='password' isRequired>
					<FormLabel htmlFor='password'>Password</FormLabel>
					<Input 
						type='password'
						variant='flushed' 
						size='sm' 
						id='password'
						name='password'
						onChange={formik.handleChange}
						value={formik.values.password}	
						/>
					<FormHelperText>Remember to never share your password with anybody</FormHelperText>
					<Checkbox isDisabled={login && password ? false : true}>Lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum lorem ipsum.</Checkbox>
				</FormControl>
				<Button colorScheme='teal' variant='outline' onClick={setLocalStorage} type='submit'>Register</Button>
				<Button colorScheme='tea' variant='ghost' onClick={clearLocalStorage}>Clear data</Button>
			</form>
			</Box>
		</Center>
	)
}


export default RegisterPage;
