import React, { useState } from 'react';
import {
	Flex,
	Box,
	Input,
	Button,
	FormLabel,
	Textarea
} from '@chakra-ui/react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const RecipeField = () => {

	const formik = useFormik({
		initialValues: {
			author: '',
			ingredients: '',
			portionSize: 1,
			steps: '',
		},
		validationSchema: Yup.object({
			author: Yup.string()
				.required('Required'),
			ingredients: Yup.string()
				.required('Required'),
			portionSize: Yup.number()
				.required('Required'),
			steps: Yup.string()			
		}),
		onSubmit: values => {
			console.log(JSON.stringify(values, null, 2))
		},
	});

	return (
		<form onSubmit={formik.handleSubmit}>
			<Flex>
				<Box>
					<FormLabel htmlFor='author'>Author</FormLabel>
					<Input 
						id='author'
						name='author'
						{...formik.getFieldProps('author')}
					/>
					{formik.touched.author && formik.errors.author ? <Box style={{color: 'red'}}>{formik.errors.author}</Box> : null}
					<FormLabel htmlFor='ingredients'>Ingredients</FormLabel>
					<Textarea 
						id='ingredients'
						name='ingredients'
						{...formik.getFieldProps('ingredients')}
					/>
					{formik.touched.ingredients && formik.errors.ingredients ? <Box style={{color: 'red'}}>{formik.errors.ingredients}</Box> : null}
					<FormLabel htmlFor='portionSize'></FormLabel>
				</Box>
			</Flex>
		</form>
	)
}

export default RecipeField;
