import React from 'react';
import {
	Flex,
	Box,
	Input,
	Button,
	FormLabel,
	Textarea,
	NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
} from '@chakra-ui/react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const RecipeField = () => {

	const formik = useFormik({
		initialValues: {
			author: '',
			ingredients: '',
			portionSize: 1,
			steps: '',
		},
		validationSchema: Yup.object({
			author: Yup.string()
				.required('Required'),
			ingredients: Yup.string()
				.required('Required'),
			portionSize: Yup.number()
				.required('Required'),
			steps: Yup.string()			
		}),
		onSubmit: values => {
			console.log(JSON.stringify(values, null, 2))
		},
	});

	const style = {color: 'red'}

	return (
		<form onSubmit={formik.handleSubmit}>
			<Flex>
				<Box width='400px' border='solid black 1px'>
					<FormLabel htmlFor='author'>Author</FormLabel>
					{formik.touched.author && formik.errors.author ? <Box style={style}>{formik.errors.author}</Box> : null}
					<Input 
						id='author'
						name='author'
						{...formik.getFieldProps('author')}
					/>
					<FormLabel htmlFor='ingredients'>Ingredients</FormLabel>
					{formik.touched.ingredients && formik.errors.ingredients ? <Box style={style}>{formik.errors.ingredients}</Box> : null}
					<Textarea 
						id='ingredients'
						name='ingredients'
						{...formik.getFieldProps('ingredients')}
					/>
					<FormLabel htmlFor='portionSize'>Portion Size</FormLabel>
					<NumberInput clampValueOnBlur={true} defaultValue={1} min={0}>
					{formik.touched.portionSize && formik.errors.portionSize ? <Box style={style}>{formik.errors.portionSize}</Box> : null}
						<NumberInputField 
							id='portionSize'
							name='portionSize'
							{...formik.getFieldProps('portionSize')}
						/>
						<NumberInputStepper>
							<NumberIncrementStepper />
							<NumberDecrementStepper />
						</NumberInputStepper>
					</NumberInput>
				</Box>
			</Flex>
		</form>
	)
}

export default RecipeField;
