import React, { useState } from 'react';
import {
	Box,
	StackDivider,
	VStack,
	Heading,
	Text,
	Flex
} from '@chakra-ui/react';
import MainContainer from './MainContainer'

// Finish styling Stack component
const Stack = (props) => (
	<VStack w='70%'>
		<StackDivider>
			<Recipe {...props}/>
		</StackDivider>
	</VStack>
)

const Recipe = (props) => {
	return (
		<Box p={5} shadow='md' borderWidth='1px' mb='3' props={props}>
			<Heading fontSize='x1'>{props.title}</Heading>
			<Flex>
				<Text mt={4}>By: {props.author}</Text>
			</Flex>
			<Flex direction='column'>
				<Text mt={4}>Ingredients: {props.ingredients.split('\n').map(item => <Box ml='6%'>{item}<br /></Box>)}</Text>
			</Flex>
			<Flex mt={4}>Portion Size: <Text fontWeight='bold' ml='4'>{props.portionSize}</Text></Flex>
			<Text mt={4}>Steps: {props.steps.split('\n').map(item => <Flex ml='6%'>{item}<br /></Flex>)}</Text>
		</Box>
	)
}

const MyRecipes = () => {

	let myLs = localStorage.getItem('recipe');
	let ls = JSON.parse(myLs)

	return (
		<>
			<MainContainer children={ls.map(recipe => <Stack title={recipe.title} author={recipe.author} ingredients={recipe.ingredients} portionSize={recipe.portionSize} steps={recipe.steps}/>)}/>
		</>
	)
}

export default MyRecipes;
