export default class RegisterPage extends React.Component {

	state = {
		login: '',
		password: ''
	}

	onChange = e => {
		this.setState({
			[e.target.name]: e.target.value
		})
	}

	render() {
		return (
			<Center>
			<Box>
				<FormControl id='login' isRequired>
					<FormLabel>Login</FormLabel>
					<Input name='login' type='login' variant='flushed' size='sm' onChange={this.onChange}/>
				</FormControl>
				<FormControl id='password' isRequired>
					<FormLabel>Password</FormLabel>
					<Input name='password' type='password' variant='flushed' size='sm' onChange={this.onChange}/>
					<FormHelperText>Remember to never share your password with anybody</FormHelperText>
				</FormControl>
				<Button colorScheme='teal' variant='outline' onClick={() => localStorage.setItem('login', 'myLogin')}>Register</Button>
			</Box>
		</Center>
		)
	}
}
