import React, { useState } from 'react';
import {
	Box,
	StackDivider,
	VStack,
	Heading,
	Text,
	Flex,
	IconButton
} from '@chakra-ui/react';
import MainContainer from './MainContainer';
import { MdClear } from 'react-icons/md';

// Finish styling Stack component
const Stack = (props) => (
	<VStack w='70%'>
		<StackDivider>
			<Recipe {...props}/>
		</StackDivider>
	</VStack>
)

// Add edit delete buttons in the top right corner
const Recipe = (props) => {
	return (
		<Flex p={5} shadow='md' borderWidth='1px' mb='3' props={props} direction='column'>
		{/* <Flex w='4px' h='4px' ml='auto' mr='3'> */}
		<Flex>
			{/* <IconButton 
				icon={<MdClear/>}
				aria-label='Delete recipe'
				size='sm'
			/> */}
		</Flex>
			<Heading fontSize='x1' justify='flex-end'>{props.title}</Heading>
			<IconButton 
				icon={<MdClear/>}
				aria-label='Delete recipe'
				size='sm'
			/>
			<Flex>
				<Text mt={4}>By: {props.author}</Text>
			</Flex>
			<Flex direction='column'>
				<Text mt={4}>Ingredients: {props.ingredients.split('\n').map(item => <Box ml='6%'>{item}<br /></Box>)}</Text>
			</Flex>
			<Text mt={4}>Portion Size: {props.portionSize}</Text>
			<Text mt={4}>Steps: {props.steps.split('\n').map(item => <Flex ml='6%'>{item}<br /></Flex>)}</Text>
		</Flex>
	)
}

const MyRecipes = () => {

	let myLs = localStorage.getItem('recipe');
	let ls = JSON.parse(myLs)

	return (
		<>
			<MainContainer children={ls.map(recipe => <Stack title={recipe.title} author={recipe.author} ingredients={recipe.ingredients} portionSize={recipe.portionSize} steps={recipe.steps}/>)}/>
		</>
	)
}

export default MyRecipes;
