import React, { useState } from 'react';
import {
	Box,
	StackDivider,
	VStack,
	Heading,
	Text,
	Grid
} from '@chakra-ui/react';
import SideContainer from './SideContainer';

const Recipe = (props) => {
	return (
		<Box p={5} shadow='md' borderWidth='1px' props={props}>
			<Heading fontSize='x1'>{props.title}</Heading>
			<Text mt={4}>{props.author}</Text>
		</Box>
	)
}

const MyRecipes = () => {

	let myLs = localStorage.getItem('recipe');
	let ls = JSON.parse(myLs)

	return (
		<Grid>
			<SideContainer />
			<VStack 
				divider={<StackDivider borderColor='gray.200' />}
				spacing={4}
				align='stretch'
			>
				<Recipe
					title={ls.title}
					author={ls.author}

				>
				</Recipe>
			</VStack>
		</Grid>
	)
}

export default MyRecipes;
