import React from 'react';
import { useFormik } from 'formik';
import { useState } from 'react';
import {
	Box,
	Center,
	// FormControl,
	FormLabel,
	Button,
	Input
} from '@chakra-ui/react';

const LoginPage = () => {

	const [isLoggedIn, setIsLoggedIn] = useState(false);

	const validate = values => {
		const errors = {};

		if (!values.login) {
			errors.login = 'Required';
		} else if (values.login !== localStorage.getItem('login')) {
			errors.login = 'Invalid login';
		}

		if (!values.password) {
			errors.password = 'Required';
		} else if (values.password !== localStorage.getItem('password')) {
			errors.password = 'Invalid password';
		}

		return errors;
	}

	const formik = useFormik({
		initialValues : {
			login: '',
			password: ''
		},
		validate,
		onSubmit: values => {
			console.log(JSON.stringify(values, null, 2))
		},
	});

	const onClickLogin = () => {
		(formik.values.login === localStorage.getItem('login') && formik.values.password === localStorage.getItem('password')) && ((formik.touched.login && !formik.errors.login) || (formik.touched.login && !formik.errors.password)) ? setIsLoggedIn(true) : <Box>Invalid login or password</Box>
	}

	if (isLoggedIn) {
		return (
			<Center>Hello user</Center>
		)
	} else {
		return (
			<Center>
				<Box>
					<form onSubmit={formik.handleSubmit}>
						<FormLabel htmlFor='login'>Login</FormLabel>
						{/* {formik.touched.login && formik.errors.login ? <Box>{formik.errors.login}</Box> : null} */}
						<Input
							id='login'
							name='login'
							type='text'
							onChange={formik.handleChange}
							onBlur={formik.handleBlur}
							value={formik.values.login}
							mb='2'
						/>
						<FormLabel htmlFor='password'>Password</FormLabel>
						{/* {formik.touched.password && formik.errors.password ? <Box>{formik.errors.password}</Box> : null} */}
						<Input 
							id='password'
							name='password'
							type='password'
							onChange={formik.handleChange}
							onBlur={formik.handleBlur}
							value={formik.values.password}
						/>
						<Button 
							colorScheme='teal' 
							variant='ghost' 
							onClick={onClickLogin}
						>
							Login
						</Button>
					</form>
				</Box>
			</Center>
		)
	}
}

export default LoginPage;
