import React, { useState } from 'react';
import {
	Box,
	StackDivider,
	VStack,
	Heading,
	Text,
	Flex
} from '@chakra-ui/react';
import MainContainer from './MainContainer'

// Finish styling Stack component
const Stack = (props) => (
	<VStack>
		<StackDivider>
			<Recipe {...props}/>
		</StackDivider>
	</VStack>
)

// Make a whole recipe Component
const Recipe = (props) => {
	return (
		<Box p={5} shadow='md' borderWidth='1px' props={props}>
			<Heading fontSize='x1'>{props.title}</Heading>
			<Flex style={{justifyContent: 'space-between'}}>
				<Text mt={4}>By: {props.author}</Text>
			</Flex>
			<Text mt={4}>Ingredients: {props.ingredients}</Text>
			<Text mt={4}>Portion Size: {props.portionSize}</Text>
			<Text mt={4}>Steps: {props.steps}</Text>
		</Box>
	)
}

const MyRecipes = () => {

	let myLs = localStorage.getItem('recipe');
	let ls = JSON.parse(myLs)

	return (
		<>
			<MainContainer children={<Stack title={ls.title} author={ls.author} ingredients={ls.ingredients} portionSize={ls.portionSize} steps={ls.steps}/>}/>
		</>
	)
}

export default MyRecipes;
